// Parse DataTable response and format all sessions for the dashboard
const items = $input.all();
console.log('Total items received:', items.length);

if (items.length === 0) {
  return [{
    json: {
      success: true,
      total_sessions: 0,
      sessions: [],
      timestamp: new Date().toISOString()
    }
  }];
}

const sessions = [];

for (const item of items) {
  try {
    const data = item.json;
    console.log('Processing item:', JSON.stringify(data).substring(0, 200));

    // Parse JSON fields if they are strings
    let answers = data.answers;
    if (typeof answers === 'string' && answers) {
      try {
        answers = JSON.parse(answers);
      } catch (e) {
        console.log('Could not parse answers:', e.message);
        answers = {};
      }
    }

    // Parse missing_fields if it's a string
    let missingFields = data.missing_fields;
    if (typeof missingFields === 'string' && missingFields) {
      try {
        missingFields = JSON.parse(missingFields);
      } catch (e) {
        console.log('Could not parse missing_fields:', e.message);
        missingFields = [];
      }
    }

    // Parse project_classification if it's a string
    let projectClassification = data.project_classification;
    if (typeof projectClassification === 'string' && projectClassification) {
      try {
        projectClassification = JSON.parse(projectClassification);
      } catch (e) {
        console.log('Could not parse project_classification:', e.message);
        projectClassification = {};
      }
    }

    sessions.push({
      session_id: data.session_id,
      requester_email: data.requester_email,
      status: data.status || 'open',
      created_at: data.created_at || data.createdAt || new Date().toISOString(),
      round_counter: data.round_counter || 0,
      max_rounds: data.max_rounds || 50,
      answers: answers || {},
      missing_fields: missingFields || [],
      project_classification: projectClassification || {},
      total_questions: Object.keys(answers || {}).length,
      has_communication: data.has_communication || false,
      has_partner_selection: data.has_partner_selection || false,
      last_updated: data.last_updated || data.updatedAt || data.created_at || data.createdAtated_at
    });
  } catch (error) {
    console.error('Error parsing session:', error.message);
  }
}

// Sort by creation date (newest first)
sessions.sort((a, b) => new Date(b.created_at) - new Date(a.created_at));

const result = {
  success: true,
  total_sessions: sessions.length,
  sessions: sessions,
  timestamp: new Date().toISOString()
};

console.log('Returning result with', sessions.length, 'sessions');

return [{ json: result }];
