  session_id: $json.session_id,
  requester_email: $json.requester_email,
  budget_range: $json.budget_range,
  preferred_partner_type: $json.preferred_partner_type,

  // Projektbeschreibung
  project_description: answers.beschreibung_vorhaben || "",
  key_points: answers.stichpunkte || "",
