  // Zeitrahmen
  timeline: answers.zeithorizont || "",
  start_date: answers.startdatum || "",
  hot_phases: answers.dauer_heisse_phasen || "",

  // Erwartungen und Anforderungen
  change_expectations: answers.erwartungen_change_begleitung || "",
  pag_need: answers.changebedarf_pag || "",

  // Strategischer Kontext
  strategic_contribution: answers.beitrag_konzernstrategie || "",
  strategic_goals: answers.strategische_ziele || "",

  // Change-Ziele
  from_state: answers.ziel_change_begleitung_von || "",
  to_state: answers.ziel_change_begleitung_zu || "",

  // Risiken und Erfolgsfaktoren
  success_blockers: answers.erfolg_verhindern || "",
  success_enablers: answers.erfolg_beitragen || "",

  // Kontakt
  contact_person: answers.ansprechpartner_name || "",
  other_notes: answers.sonstiges || ""
};

return { json: projectData };

