// Speichere Requirements und gib Projektdaten weiter
const projectData = $items('Extract Project Data')[0].json;
const aiOutput = $input.first().json;

// Text aus AI-Output extrahieren
let requirementsText = '';
if (aiOutput.message && aiOutput.message.content) {
  requirementsText = aiOutput.message.content;
} else if (aiOutput.text) {
  requirementsText = aiOutput.text;
} else if (typeof aiOutput === 'string') {
  requirementsText = aiOutput;
}

return {
  json: {
    ...projectData,
    requirements_profile: requirementsText
  }
};

// Speichere RFP und gib alles weiter
const prevData = $items('Store Requirements')[0].json;
const aiOutput = $input.first().json;

let rfpText = '';
if (aiOutput.message && aiOutput.message.content) {
  rfpText = aiOutput.message.content;
} else if (aiOutput.text) {
  rfpText = aiOutput.text;
} else if (typeof aiOutput === 'string') {
  rfpText = aiOutput;
}

return {
  json: {
    ...prevData,
    rfp_document: rfpText
  }
};

// Speichere Evaluation und gib alles weiter
const prevData = $items('Store RFP')[0].json;
const aiOutput = $input.first().json;

let evaluationText = '';
if (aiOutput.message && aiOutput.message.content) {
  evaluationText = aiOutput.message.content;
} else if (aiOutput.text) {
  evaluationText = aiOutput.text;
} else if (typeof aiOutput === 'string') {
  evaluationText = aiOutput;
}

return {
  json: {
    ...prevData,
    evaluation_criteria: evaluationText
  }
};

// Speichere Process Guide und erstelle finales Dokument
const prevData = $items('Store Evaluation')[0].json;
const aiOutput = $input.first().json;

let processGuideText = '';
if (aiOutput.message && aiOutput.message.content) {
  processGuideText = aiOutput.message.content;
} else if (aiOutput.text) {
  processGuideText = aiOutput.text;
} else if (typeof aiOutput === 'string') {
  processGuideText = aiOutput;
}

const sessionId = prevData.session_id;

const documentContent = `
# EXTERNE PARTNER-AUSWAHL
## Change Management Unterstützung
### Session: ${sessionId}

---

# 1. ANFORDERUNGSPROFIL

${prevData.requirements_profile}

---

# 2. REQUEST FOR PROPOSAL (RFP)
## Ausschreibungsunterlage

${prevData.rfp_document}

---

# 3. BEWERTUNGSKRITERIEN
## Evaluationsraster für Angebote

${prevData.evaluation_criteria}

---

# 4. PROZESS-LEITFADEN
## Auswahl und Onboarding

${processGuideText}

---

**Erstellt am:** ${new Date().toLocaleString('de-DE')}
**Basis:** Change-Projekt Session ${sessionId}

---

## NÄCHSTE SCHRITTE:

1. ☐ Dokumente finalisieren und intern abstimmen
2. ☐ Budget und Freigaben sicherstellen
3. ☐ Ausschreibung veröffentlichen
4. ☐ Bewertungsteam zusammenstellen
5. ☐ Timeline für Auswahl festlegen
`;

return {
  json: {
    session_id: sessionId,
    document_content: documentContent,
    filename: `Partner-Auswahl_RFP_${sessionId}_${new Date().toISOString().split('T')[0]}.md`,
    requirements_profile: prevData.requirements_profile,
    rfp_document: prevData.rfp_document,
    evaluation_criteria: prevData.evaluation_criteria,
    process_guide: processGuideText
  }
};
